import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Navlink, Route, useParams, Switch } from 'react-router-dom'
import './Songs.css'
import Navigation from "../Navigation";
import SignupFormPage from "../SignupFormPage";
import Search from "../Search"

import * as sessionActions from "../../store/session";
import * as songActions from "../../store/songs";

function ViewSongs() {
    const dispatch = useDispatch();
    const [isLoaded, setIsLoaded] = useState(false);
    const [isUser, setIsUser] = useState(false)
    const [showForm, setShowForm] = useState(false)

    useEffect(() => {
        dispatch(sessionActions.restoreUser()).then(() => setIsLoaded(true));
    }, [dispatch]);

    useEffect(() => {
        dispatch(songActions.getSongs())
    }, [dispatch])

    let currentUser = useSelector((state) => state.session.user)


    useEffect(() => {
        if (currentUser !== undefined) {
            setIsUser(true)
        }
        else {
            setIsUser(false)
        }
    }, [currentUser])


    const handleSubmit = async (e) => {
        e.preventDefault();




        const payload = {
          title,
          artist,
          releaseDate,
          songPath,
          imagePath,
          userId: sessionUser.id,
          albumName,
          albumId,
        };

        let newSong;
        try {
          console.log('COMPONENT TRY BLOCK - BEFORE DISPATCH - THIS IS PAYLOAD ->', payload)
          newSong = await dispatch(createSong(payload));
          console.log('COMPONENT TRY BLOCK - AFTER DISPATCH - THIS IS createdPokemon ->', createSong)
        } catch (error) {
          if (error instanceof ValidationError) setErrorMessages(error.errors);
          else setErrorMessages({ overall: error.toString().slice(7) });
        }
        if (newSong) {
          setErrorMessages({});
          history.push(`/song/${newSong.id}`);
        }
      };


    return (
        <app>
            <div className='container'>
                <div id='image-div'>
                    <div className="navigation">
                        <Navigation isLoaded={isLoaded} />
                        {isLoaded && (
                            <Switch>
                             <Route path="/signup">
                                    <SignupFormPage />
                                </Route>
                                <Route path="/" exact>

                                </Route>
                            </Switch>
                     )}
                    </div>
                </div>
                <div className="SearchBarHeader">
                    <span className = "SearchBar">
                        <span>
                            <Search />

                        </span>
                    </span>




                </div>

            </div>




        </app>


    )
}

export default ViewSongs
